INSERT INTO medal_of_honor VALUES (-1, 'Wisconsin', -1, -1, -1, 29, 1, 1925, 'For conspicuous gallantry and intrepidity at the risk of his life above and beyond the call of duty while serving with the 4th Battalion, 10th Marines, 2d Marine Division, in action against enemy Japanese forces on Saipan, Marianas Islands, 7 July 1944. When the enemy launched a fierce, determined counterattack against our positions and overran a neighboring artillery battalion, Pfc. Agerholm immediately volunteered to assist in the efforts to check the hostile attack and evacuate our wounded. Locating and appropriating an abandoned ambulance jeep, he repeatedly made extremely perilous trips under heavy rifle and mortar fire and single-handedly loaded and evacuated approximately 45 casualties, working tirelessly and with utter disregard for his own safety during a grueling period of more than 3 hours. Despite intense, persistent enemy fire, he ran out to aid 2 men whom he believed to be wounded marines but was himself mortally wounded by a Japanese sniper while carrying out his hazardous mission. Pfc. Agerholm''s brilliant initiative, great personal valor and self-sacrificing efforts in the face of almost certain death reflect the highest credit upon himself and the U.S. Naval Service. He gallantly gave his life for his country.', '', TRUE, '', '', '-1--1--1', '?', 15, 'http://www.cmohs.org/recipient-detail/2615/agerholm-harold-christ.php', ' Racine, Wis.', 'United States District Court For The Northern Mariana Islands, Kopa Di Oru Street, Garapan, Saipan 96950, CNMI', 145, 'Agerholm, Harold Christ', 'U.S. Marine Corps', 'Private First Class');
INSERT INTO medal_of_honor VALUES (45, '', 19, 6, 1864, -1, -1, -1, 'Served on board the U.S.S. Kearsarge when she destroyed the Alabama off Cherbourg, France, 19 June 1864. Carrying out his duties courageously, PmS. Aheam exhibited marked coolness and good conduct and was highly recommended by his divisional officer for gallantry under enemy fire.', '', TRUE, '', 'France', '1864-6-19', '?', 49, 'http://www.cmohs.org/recipient-detail/17/aheam-michael.php', '', 'Cherbourg, France', -1, 'Aheam, Michael', 'U.S. Navy', 'Paymaster''s Steward');
INSERT INTO medal_of_honor VALUES (482, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Puritan at the time of the collapse of one of the crown sheets of boiler E of that vessel, 1 July 1897. Wrapped in wet cloths to protect his face and arms, Ahern entered the fireroom, crawled over the tops of the boilers and closed the auxiliary stop valve, disconnecting boiler E and removing the danger of disabling the other boilers.', '', TRUE, '', '', '-1--1--1', '?', -1, 'http://www.cmohs.org/recipient-detail/1987/ahern-william.php', '1861, Ireland', '', -1, 'Ahern, William', 'U.S. Navy', 'Watertender');
