INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, -1, -1, -1, '* * * By virtue of the authority vested by law in the President of the United States, the Congressional Medal of Honor, emblem of the highest military ideals and virtues, is bestowed in the name of the Congress of the United States upon the unknown, unidentified Belgian soldier in a desire to add all that is possible to the imperishable glory won by the soldiers of Belgium who fought as comrades of the American soldiers during the World War, and to commemorate with them the deeds of the nations associated with the United States of America, by paying this tribute to their unknown dead (A.G. 220.523) (War Department General Orders, No. 52, I Dec. 1922, Sec. 1).', '', TRUE, '', '', '-1--1--1', '?', -1, 'http://www.cmohs.org/recipient-detail/3463/belgium-unknown.php', '', '', -1, 'Belgium, Unknown', 'U.S. Army', 'Unknown');
