INSERT INTO medal_of_honor VALUES (11, 'New York', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Varuna in one of the most responsible positions, during the attacks on Forts Jackson and St. Philip, and while in action against the rebel ship Morgan, 24 April 1862. Although guns were raking the decks from behind him, Bradley remained steadfast at the wheel throughout the thickest of the fight, continuing at his station and rendering service with the greatest courage until his ship, repeatedly holed and twice rammed by the rebel ship Morgan, was beached and sunk.', '', TRUE, '', '', '-1--1--1', '?', 44, 'http://www.cmohs.org/recipient-detail/138/bradley-amos.php', '1837, Dansville, N.Y', 'Fort Jackson, NY 12965, USA', -74, 'Bradley, Amos', 'U.S. Navy', 'Landsman');
