INSERT INTO medal_of_honor VALUES (-1, 'North Dakota', -1, -1, -1, 28, 6, 1884, 'For extraordinary heroism and devotion to duty while serving on the U.S.S. Pittsburgh, at the time of an accidental explosion of ammunition on that vessel. On 23 July 1917, some saluting cartridge cases were being reloaded in the after casemate: through an accident an explosion occurred. Comdr. Bradley (then Lieutenant), who was about to enter the casemate, was blown back by the explosion and rendered momentarily unconscious, but while still dazed, crawled into the casemate to extinguish burning materials in dangerous proximity to a considerable amount of powder, thus preventing further explosions.', '', TRUE, '', '', '-1--1--1', '?', -1, 'http://www.cmohs.org/recipient-detail/2507/bradley-willis-winter-jr.php', ' Ransomville, N.Y.', '', -1, 'Bradley, Willis Winter, Jr.', 'U.S. Navy', 'Commander');
