INSERT INTO medal_of_honor VALUES (11, '', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Brooklyn in the attack upon Forts Jackson and St. Philip and at the taking of New Orleans, 24 and 25 April 1862. Although severely wounded by a heavy splinter, Buck continued to perform his duty until positively ordered below. Later stealing back to his post, he steered the ship for 8 hours despite his critical condition. His bravery was typical of the type which resulted in the taking of the Forts Jackson and St. Philip and in the capture of New Orleans.', '', TRUE, '', '', '-1--1--1', '?', 29, 'http://www.cmohs.org/recipient-detail/183/buck-james.php', '1808, Baltimore, Md.', 'Fort Jackson, Buras-Triumph, LA 70041, USA', -89, 'Buck, James', 'U.S. Navy', 'Quartermaster');
