INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Lackawanna during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay, on 5 August 1864. Although severely wounded and sent below under the surgeon''s charge, Burns promptly returned to his station and assisted the powder division throughout the prolonged action which resulted in the capture of the rebel ram Tennessee and in the damaging and destruction of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/200/burns-john-m.php', '1835, Hudson, N.Y.', 'Fort Morgan, 51 Alabama 180, Gulf Shores, AL 36542, USA', -88, 'Burns, John M.', 'U.S. Navy', 'Seaman');
