INSERT INTO medal_of_honor VALUES (-1, 'California', -1, -1, -1, 26, 7, 1892, 'For extraordinary heroism and conspicuous intrepidity above and beyond the call of duty during action against enemy Japanese forces off Savo Island on the night of 12-13 November 1942. Although out-balanced in strength and numbers by a desperate and determined enemy, Rear Adm. Callaghan, with ingenious tactical skill and superb coordination of the units under his command, led his forces into battle against tremendous odds, thereby contributing decisively to the rout of a powerful invasion fleet, and to the consequent frustration of a formidable Japanese offensive. While faithfully directing close-range operations in the face of furious bombardment by superior enemy fire power, he was killed on the bridge of his flagship. His courageous initiative, inspiring leadership, and judicious foresight in a crisis of grave responsibility were in keeping with the finest traditions of the U.S. Naval Service. He gallantly gave his life in the defense of his country.', '', TRUE, '', 'Oakland, Calif.', '-1--1--1', '?', -9, 'http://www.cmohs.org/recipient-detail/2665/callaghan-daniel-judson.php', ' San Francisco, Calif.', 'Savo Island, Solomon Islands', 159, 'Callaghan, Daniel Judson', 'U.S. Navy', 'Rear Admiral');
