INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, 5, 11, 1915, 'For distinguished conduct in the line of his profession, extraordinary courage and disregard of his own condition during the bombardment of Sand Island, Midway Islands, by Japanese forces on 7 December 1941. 1st Lt. Cannon, Battery Commander of Battery H, 6th Defense Battalion, Fleet Marine Force, U.S. Marine Corps, was at his command post when he was mortally wounded by enemy shellfire. He refused to be evacuated from his post until after his men who had been wounded by the same shell were evacuated, and directed the reorganization of his command post until forcibly removed. As a result of his utter disregard of his own condition he died from loss of blood.', '', TRUE, '', 'Michigan', '-1--1--1', '?', -1, 'http://www.cmohs.org/recipient-detail/2667/cannon-george-ham.php', ' Webster Groves, Mo.', 'Unknown', -1, 'Cannon, George Ham', 'U.S. Marine Corps', 'First Lieutenant');
