INSERT INTO medal_of_honor VALUES (-1, '', 19, 9, 1863, -1, -1, -1, 'At a critical stage in the battle when the 14th Corps lines were wavering and in disorder he on his own initiative bugled "to the colors" amid the 18th U.S. Infantry who formed by him, and held the enemy. Within a few minutes he repeated his action amid the wavering 2d Ohio Infantry. This bugling deceived the enemy who believed reinforcements had arrived. Thus, they delayed their attack.', 'Company E, 1st Battalion', TRUE, '15th U.S. Infantry', 'North Greenfield, Ohio', '1863-9-19', '?', 34, 'http://www.cmohs.org/recipient-detail/228/carson-william-j.php', 'Washington County, Pa.', 'Chickamauga, GA, USA', -85, 'Carson, William J.', 'U.S. Army', 'Musician');
