INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Brooklyn during action against rebel forts and gunboats and with the ram Tennessee, in Mobile Bay, 5 August 1864. Despite severe damage to his ship and the loss of several men on board as enemy fire raked her decks from stem to stern, Cooper fought his gun with skill and courage throughout the furious battle which resulted in the surrender of the prize rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.INSERT INTO medal_of_honor VALUES (-1, '', 13, 12, 1862, 12, 4, 1840, 'Seized the regimental colors, the color bearer having been shot down, and, waving them, rallied the regiment under a heavy fire.', 'Company C', TRUE, '9th New Hampshire Infantry', 'Nashua, N.H.', '1862-12-13', '?', 38, 'http://www.cmohs.org/recipient-detail/292/copp-charles-d.php', ' Warren County, N.H.', 'Fredericksburg, VA, USA', -77, 'Copp, Charles D.', 'U.S. Army', 'Second Lieutenant');
