INSERT INTO medal_of_honor VALUES (17, 'New York', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Cincinnati during the attack on the Vicksburg batteries and at the time of her sinking. Engaging the enemy in a fierce battle, the Cincinnati, amidst an incessant fire of shot and shell, continued to fire her guns to the last, though so penetrated by shellfire that her fate was sealed. Serving bravely during this action, Corcoran was conspicuously cool under the fire of the enemy, never ceasing to fight until this proud ship went down, "her colors nailed to the mast."', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/294/corcoran-thomas-e.php', '1838, New York', 'Vicksburg, MS, USA', -90, 'Corcoran, Thomas E.', 'U.S. Navy', 'Landsman');
