INSERT INTO medal_of_honor VALUES (-1, 'Florida', 2, 10, 1920, 5, 10, 1889, 'For heroic service in attempting to rescue a brother officer from a flame-enveloped airplane. On 2 October 1920, an airplane in which Lt. Comdr. Corry was a passenger crashed and burst into flames. He was thrown 30 feet clear of the plane and, though injured, rushed back to the burning machine and endeavored to release the pilot. In so doing he sustained serious burns, from which he died 4 days later.', '', TRUE, '', '', '1920-10-2', '?', 41, 'http://www.cmohs.org/recipient-detail/2481/corry-william-merrill-jr.php', ' Quincy, Fla.', 'Hartford, CT, USA', -72, 'Corry, William Merrill, Jr.', 'U.S. Navy', 'Lieutenant Commander');
