INSERT INTO medal_of_honor VALUES (11, 'New York', -1, -1, -1, -1, -1, -1, 'Cotton served on board the U.S.S. Baron De Kalb in the Yazoo River expedition, 23 to 27 December 1862. Proceeding under orders up the Yazoo River, the Baron De Kalb, with the object of capturing or destroying the enemy''s transports, came upon the steamers John Walsh, R. J. Locklan, Golden Age and the Scotland, sunk on a bar where they were ordered to be burned. Continuing up the river, the Baron De Kalb was fired upon but, upon returning the fire, caused the enemy''s retreat. Returning down the Yazoo, she destroyed and captured large quantities of enemy equipment and several prisoners. Serving bravely throughout this action, Cotton, as coxswain "distinguished himself in the various actions."', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/300/cotton-peter.php', '1839, New York, N.Y.', 'Yazoo River, 1, LA, USA', -90, 'Cotton, Peter', 'U.S. Navy', 'Ordinary Seaman');
