INSERT INTO medal_of_honor VALUES (-1, '', 13, 5, 1939, 2, 2, 1903, 'For extraordinary heroism in the line of his profession as a master diver throughout the rescue and salvage operations following the sinking of the U.S.S. Squalus on 23 May 1939. His leadership and devotion to duty in directing diving operations and in making important and difficult dives under the most hazardous conditions characterize conduct far above and beyond the ordinary call of duty.', '', TRUE, '', 'Connecticut', '1939-5-13', '?', -1, 'http://www.cmohs.org/recipient-detail/2482/crandall-orson-l.php', ' St. Joseph, Mo.', '', -1, 'Crandall, Orson L.', 'U.S. Navy', 'Chief Boatswain''s Mate');
