INSERT INTO medal_of_honor VALUES (-1, 'New Jersey', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Valley City during action against rebel fort batteries and ships off Elizabeth City, N.C., on 10 February 1862. When a shell from the shore penetrated the side and passed through the magazine, exploding outside the screen on the berth deck, several powder division protecting bulkheads were torn to pieces and the forward part of the berth deck set on fire. Showing great presence of mind, Davis courageously covered a barrel of powder with his own body and prevented an explosion, while at the same time passing powder to provide the division on the upper deck while under fierce enemy fire.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/335/davis-john.php', 'Cedarville, N.J.', 'Elizabeth City, NC, USA', -76, 'Davis, John', 'U.S. Navy', 'Quarter Gunner');
