INSERT INTO medal_of_honor VALUES (176, 'New York', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Kansas near Greytown, Nicaragua, 12 April 1872. Displaying great coolness and self-possession at the time Comdr. A. F. Crosman and others were drowned, Denham, by heroism and personal exertion, prevented greater loss of life.', '', TRUE, '', '', '-1--1--1', '?', 10, 'http://www.cmohs.org/recipient-detail/2008/denham-austin.php', '1849, England', 'Greytown, Nicaragua', -83, 'Denham, Austin', 'U.S. Navy', 'Seaman');
INSERT INTO medal_of_honor VALUES (45, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Brooklyn during action against rebel forts and gunboats and with the ram Tennessee, in Mobile Bay, 5 August 1864. Despite severe damage to his ship and the loss of several men on board as enemy fire raked her decks, Sgt. Denig fought his gun with skill and courage throughout the furious 2-hour battle which resulted in the surrender of the rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/351/denig-john-henry.php', '1839, York, Pa', 'Mobile Bay, Alabama, USA', -87, 'Denig, John Henry', 'U.S. Marine Corps', 'Sergeant');
INSERT INTO medal_of_honor VALUES (45, '', -1, -1, -1, -1, -1, -1, 'Denning served on board the U.S. Picket Boat No. 1 in action, 27 October 1864, against the Confederate ram Albemarle which had resisted repeated attacks by our steamers and had kept a large force of vessels employed in watching her. The picket boat, equipped with a spar torpedo, succeeded in passing the enemy pickets within 20 yards without being discovered and then made for the Albemarle under a full head of steam. Immediately taken under fire by the ram, the small boat plunged on, jumped the log boom which encircled the target and exploded its torpedo under the port bow of the ram. The picket boat was destroyed by enemy fire and almost the entire crew taken prisoner or lost.', '', TRUE, '', 'New York, N.Y.', '-1--1--1', '?', 37, 'http://www.cmohs.org/recipient-detail/352/denning-lorenzo.php', '1843, Connecticut', 'United States', -95, 'Denning, Lorenzo', 'U.S. Navy', 'Landsman');
INSERT INTO medal_of_honor VALUES (-1, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Brooklyn during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay, on 5 August 1864. Despite severe damage to his ship and the loss of several men on board as enemy fire raked her decks from stem to stern, Dennis displayed outstanding skill and courage in operating the torpedo catcher and in assisting in working the bow chasers throughout the furious battle which resulted in the surrender of the prize rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/353/dennis-richard.php', '1826, Massachusetts', 'Fort Morgan, 51 Alabama 180, Gulf Shores, AL 36542, USA', -88, 'Dennis, Richard', 'U.S. Navy', 'Boatswain''s Mate');
