INSERT INTO medal_of_honor VALUES (71, 'New York', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Metacomet. As a member of the boat''s crew which went to the rescue of the U.S. Monitor Tecumseh when that vessel was struck by a torpedo in passing the enemy forts in Mobile Bay, 5 August 1864, Donnelly braved the enemy fire which was said by the admiral to be "one of the most galling" he had ever seen and aided in rescuing from death 10 of the crew of the Tecumseh, eliciting the admiration of both friend and foe.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/369/donnelly-john.php', '1839, England', 'Mobile Bay, Alabama, USA', -87, 'Donnelly, John', 'U.S. Navy', 'Ordinary Seaman');
