INSERT INTO medal_of_honor VALUES (17, 'Illinois', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Cincinnati during the attack on the Vicksburg batteries and at the time of her sinking, 27 May 1863. Engaging the enemy in a fierce battle, the Cincinnati, amidst an incessant fire of shot and shell, continued to fire her guns to the last, though so penetrated by enemy shellfire that her fate was sealed. Serving courageously throughout this action, Dow carried out his duties to the end on this proud ship that went down with "her colors nailed to the mast."', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/382/dow-henry.php', '1840, Scotland', 'Vicksburg, MS, USA', -90, 'Dow, Henry', 'U.S. Navy', 'Boatswain''s Mate');
