INSERT INTO medal_of_honor VALUES (59, 'Maine', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Monadnock in action during several attacks on Fort Fisher, 24 and 25 December 1864; and 13, 14, and 15 January 1865. With his ship anchored well inshore to insure perfect range against the severe fire of rebel guns, Dunn continued his duties when the vessel was at anchor, as her propellers were kept in motion to make her turrets bear, and the shooting away of her chain might have caused her to ground. Disdainful of shelter despite severe weather conditions, he inspired his shipmates and contributed to the success of his vessel in reducing the enemy guns to silence.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/391/dunn-william.php', 'Maine', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Dunn, William', 'U.S. Navy', 'Quartermaster');
