INSERT INTO medal_of_honor VALUES (-1, '', 18, 9, 1879, -1, -1, -1, 'Lt. Emmet was in G Troop which was sent to relieve a detachment of soldiers under attack by hostile Apaches During a flank attack on the Indian camp, made to divert the hostiles Lt. Emmet and 5 of his men became surrounded when the Indians returned to defend their camp. Finding that the Indians were making for a position from which they could direct their fire on the retreating troop, the Lieutenant held his point with his party until the soldiers reached the safety of a canyon. Lt. Emmet then continued to hold his position while his party recovered their horses. The enemy force consisted of approximately 200.', '', TRUE, '9th U.S. Cavalry', 'New York, N.Y.', '1879-9-18', '?', 36, 'http://www.cmohs.org/recipient-detail/1635/emmet-robert-temple.php', 'New York, N.Y.', 'Canyon Drive, Raton, NM 87740, USA', -104, 'Emmet, Robert Temple', 'U.S. Army', 'Second Lieutenant');
