INSERT INTO medal_of_honor VALUES (-1, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Ranger off Ensenada, Mexico, 18 January 1886. Jumping overboard from that vessel, Enright rescued John Bell, ordinary seaman, and George Svensson, ordinary seaman, from drowning.', '', TRUE, '', '', '-1--1--1', '?', 31, 'http://www.cmohs.org/recipient-detail/2011/enright-john.php', '1864, Lynn, Mass.', 'Ensenada, Baja California, Mexico', -116, 'Enright, John', 'U.S. Navy', 'Landsman');
INSERT INTO medal_of_honor VALUES (-1, 'Ohio', -1, -1, -1, 14, 7, 1923, 'For conspicuous gallantry and intrepidity at the risk of his life above and beyond the call of duty while serving with the 1st Battalion, 6th Marines, 2d Marine Division, in action against enemy Japanese forces on the Island of Saipan in the Marianas, on 25 June 1944. With his machinegun emplacement bearing the full brunt of a fanatic assault initiated by the Japanese under cover of predawn darkness, Pfc. Epperson manned his weapon with determined aggressiveness, fighting furiously in the defense of his battalion''s position and maintaining a steady stream of devastating fire against rapidly infiltrating hostile troops to aid materially in annihilating several of the enemy and in breaking the abortive attack. Suddenly a Japanese soldier, assumed to be dead, sprang up and hurled a powerful hand grenade into the emplacement. Determined to save his comrades, Pfc. Epperson unhesitatingly chose to sacrifice himself and, diving upon the deadly missile, absorbed the shattering violence of the exploding charge in his own body. Stouthearted and indomitable in the face of certain death, Pfc. Epperson fearlessly yielded his own life that his able comrades might carry on the relentless battle against a ruthless enemy. His superb valor and unfaltering devotion to duty throughout reflect the highest credit upon himself and upon the U.S. Naval Service. He gallantly gave his life for his country.', '', TRUE, '', '', '-1--1--1', '?', 15, 'http://www.cmohs.org/recipient-detail/2727/epperson-harold-glenn.php', ' Akron, Ohio', 'United States District Court For The Northern Mariana Islands, Kopa Di Oru Street, Garapan, Saipan 96950, CNMI', 145, 'Epperson, Harold Glenn', 'U.S. Marine Corps', 'Private First Class');
INSERT INTO medal_of_honor VALUES (59, 'New York', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Pontoosuc during the capture of Fort Fisher and Wilmington, 24 December 1864, to 22 February 1865. Carrying out his duties faithfully throughout this period, Erickson was so severely wounded in the assault upon Fort Fisher that he was sent to the hospital at Portsmouth, Va. Erickson was recommended for his gallantry, skill, and coolness in action while under the fire of the enemy.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/414/erickson-john-p.php', 'London, England', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Erickson, John P.', 'U.S. Navy', 'Captain of the Forecastle');
