INSERT INTO medal_of_honor VALUES (11, 'Maine', -1, -1, -1, -1, -1, -1, 'Served on board the U.S. Steam Gunboat Pinola during action against Forts Jackson and St. Philip, and during the taking of New Orleans, 24 April 1862. While engaged in the bombardment of Fort St. Philip, Frisbee, acting courageously and without personal regard, closed the powder magazine which had been set afire by enemy shelling and shut off his avenue of escape, thereby setting a high example of bravery. He served courageously throughout these engagements which resulted in the taking of the Forts Jackson and St. Philip and in the surrender of New Orleans.', '', TRUE, '', '', '-1--1--1', '?', 44, 'http://www.cmohs.org/recipient-detail/469/frisbee-john-b.php', '1822, Maine', 'Fort Jackson, NY 12965, USA', -74, 'Frisbee, John B.', 'U.S. Navy', 'Gunner''s Mate');
