INSERT INTO medal_of_honor VALUES (11, '', -1, -1, -1, -1, -1, -1, 'Captain of a gun on board the U.S.S. Varuna during the attacks on Forts Jackson and St. Philip, and while under fire and ramming by the rebel ship Morgan, 24 April 1862. During this action at extremely close range while his ship was under furious fire and twice rammed by the rebel ship Morgan, Greene remained steadfast at his gun throughout the thickest of the fight and was instrumental in inflicting damage on the enemy until the Varuna, badly damaged and forced to beach, was finally sunk.', '', TRUE, '', 'New York', '-1--1--1', '?', 44, 'http://www.cmohs.org/recipient-detail/523/greene-john.php', '', 'Fort Jackson, NY 12965, USA', -74, 'Greene, John', 'U.S. Navy', 'Captain of the Forecastle');
