INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, 1, 7, 1841, 'At Williamsburg, Va., voluntarily carried a severely wounded comrade off the field in the face of a large force of the enemy; in doing so was himself severely wounded and taken prisoner. Went into the fight at Bristol Station, Va., although severely disabled. At Manassas, volunteered to search the woods for the wounded.', 'Company G', TRUE, '72d New York Infantry', 'Westfield, N.Y.', '-1--1--1', '?', 38, 'http://www.cmohs.org/recipient-detail/543/haight-john-h.php', ' Westfield, N.Y.', 'Manassas, VA, USA', -77, 'Haight, John H.', 'U.S. Army', 'Sergeant');
