INSERT INTO medal_of_honor VALUES (59, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Minnesota in action during the assault on Fort Fisher, 15 January 1865. Landing on the beach with the assaulting party from his ship, Harcourt advanced to the top of the sandhill and partly through the breach in the palisades despite enemy fire which killed and wounded many officers and men. When more than two-thirds of the men become seized with panic and retreated on the run, he remained with the party until dark when it came safely away, bringing its wounded, its arms and its colors.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/565/harcourt-thomas.php', '1841, Boston, Mass.', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Harcourt, Thomas', 'U.S. Navy', 'Ordinary Seaman');
