INSERT INTO medal_of_honor VALUES (-1, '', 24, 7, 1863, -1, -1, -1, 'While in command of a squadron in rear guard of a cavalry division, then retiring before the advance of a corps of infantry, was attacked by the enemy and, orders having been given to abandon the guns of a section of field artillery with the rear guard that were in imminent danger of capture, he disregarded the orders received and aided in repelling the attack and saving the guns.', 'Company M', TRUE, '5th Michigan Cavalry', '', '1863-7-24', '?', 37, 'http://www.cmohs.org/recipient-detail/585/hastings-smith-h.php', 'Quincy, Mich.', 'Virginia, USA', -78, 'Hastings, Smith H.', 'U.S. Army', 'Captain');
