INSERT INTO medal_of_honor VALUES (45, 'New Hampshire', -1, -1, -1, -1, -1, -1, 'Hawkins served on board the U.S.S. Agawam, as one of a volunteer crew of a powderboat which was exploded near Fort Fisher, 23 December 1864. The powderboat, towed in by the Wilderness to prevent detection by the enemy, cast off and slowly steamed to within 300 yards of the beach. After fuses and fires had been lit and a second anchor with short scope let go to assure the boat''s tailing inshore, the crew again boarded the Wilderness and proceeded a distance of 12 miles from shore. Less than 2 hours later the explosion took place, and the following day fires were observed still burning at the forts.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/589/hawkins-charles.php', '1834, Scotland', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Hawkins, Charles', 'U.S. Navy', 'Seaman');
INSERT INTO medal_of_honor VALUES (-1, '', 2, 4, 1865, -1, -1, -1, 'When the lines were wavering from the well-directed fire of the enemy, this officer, acting adjutant of the regiment, sprang forward, and with encouraging words cheered the soldiers on and, although dangerously wounded, refused to leave the field until the enemy''s works were taken.', 'Company E', TRUE, '3d Vermont Infantry', 'Woodstock, Vt.', '1865-4-2', '?', 37, 'http://www.cmohs.org/recipient-detail/590/hawkins-gardner-c.php', 'Pomfret, Vt.', 'Petersburg, VA, USA', -77, 'Hawkins, Gardner C.', 'U.S. Army', 'First Lieutenant');
INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, -1, -1, -1, 'One of the 19 of 22 men (including 2 civilians) who, by direction of Gen. Mitchell (or Buell), penetrated nearly 200 miles south into enemy territory and captured a railroad train at Big Shanty, Ga., in an attempt to destroy the bridges and track between Chattanooga and Atlanta.', 'Company A', TRUE, '33d Ohio Infantry', 'Portsmouth, Scioto County, Ohio', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/591/hawkins-martin-j.php', 'Mercer County, Pa.', 'Georgia, USA', -82, 'Hawkins, Martin J.', 'U.S. Army', 'Corporal');
