INSERT INTO medal_of_honor VALUES (-1, '', 23, 7, 1898, -1, -1, -1, 'After 2 men had been shot down by Spaniards while transmitting orders to the engine-room on the Wanderer, the ship having become disabled, this officer took the position held by them and personally transmitted the orders, remaining at his post until the ship was out of danger.', '', TRUE, '3d U.S. Cavalry', 'Mississippi', '1898-7-23', '?', 22, 'http://www.cmohs.org/recipient-detail/2128/heard-john-w.php', 'Mississippi', 'Bahia Honda, Cuba', -83, 'Heard, John W.', 'U.S. Army', 'First Lieutenant');
