INSERT INTO medal_of_honor VALUES (-1, '', 4, 6, 1863, 23, 5, 1843, 'When men were needed to oppose a superior Confederate force he laid down his drum for a rifle and proceeded to the front of the skirmish line which was about 120 feet from the enemy. While on this volunteer mission and firing at the enemy he was hit in the head with a minie ball which passed through him. An order was given to "lay him in the shade; he won''t last long." He recovered from this wound being left with an ugly scar.', 'Company A', TRUE, '8th Wisconsin Infantry', 'Waupaca Township, Wis.', '1863-6-4', '?', 32, 'http://www.cmohs.org/recipient-detail/621/hilliker-benjamin-f.php', ' Golden, Erie County, N.Y.', 'Mechanicsburg, Mississippi 39040, USA', -90, 'Hilliker, Benjamin F.', 'U.S. Army', 'Musician');
