INSERT INTO medal_of_honor VALUES (59, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'Served as gunner''s mate on board the U.S.S. Montauk, 21 September 1864. During the night of 21 September, when fire was discovered in the magazine lightroom of the vessel, causing a panic and demoralizing the crew, Horton rushed into the cabin, obtained the magazine keys, sprang into the lightroom and began passing out combustibles, Including the box of signals in which the fire originated.', '', TRUE, '', '', '-1--1--1', '?', -1, 'http://www.cmohs.org/recipient-detail/647/horton-james.php', '1838, Massachusetts', '', -1, 'Horton, James', 'U.S. Navy', 'Gunner''s Mate');
