INSERT INTO medal_of_honor VALUES (59, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Rhode Island, which was engaged in saving the lives of the officers and crew of the Monitor, 30 December 1862. Participating in the hazardous task of rescuing the officers and crew of the sinking Monitor, Horton, after rescuing several of the men, became separated in a heavy gale with other members of the cutter that had set out from the Rhode Island and spent many hours in the small boat at the mercy of the weather and high seas until finally picked up by a schooner 50 miles east of Cape Hatteras.', '', TRUE, '', '', '-1--1--1', '?', 35, 'http://www.cmohs.org/recipient-detail/648/horton-lewis-a.php', '1839, Bristol Mass.', 'Cape Hatteras, North Carolina, USA', -75, 'Horton, Lewis A.', 'U.S. Navy', 'Seaman');
