INSERT INTO medal_of_honor VALUES (45, 'Alabama', -1, -1, -1, -1, -1, -1, 'Houghton served on board the U.S. Picket Boat No. 1 in action, 27 October 1864, against the Confederate ram Albemarle, which had resisted repeated attacks by our steamers and had kept a large force of vessels employed in watching her. The picket boat, equipped with a spar torpedo, succeeded in passing the enemy pickets within 20 yards without being discovered and then made for the Albemarle under a full head of steam. Immediately taken under fire by the ram, the small boat plunged on, jumped the log boom which encircled the target and exploded its torpedo under the port bow of the ram. The picket boat was destroyed by enemy fire and almost the entire crew taken prisoner or lost.', '', TRUE, '', '', '-1--1--1', '?', 37, 'http://www.cmohs.org/recipient-detail/652/houghton-edward-j.php', '1843, Mobile, Ala.', 'United States', -95, 'Houghton, Edward J.', 'U.S. Navy', 'Ordinary Seaman');
