INSERT INTO medal_of_honor VALUES (-1, '', 11, 6, 1928, -1, -1, -1, 'For display of extraordinary heroism in the line of his profession on 11 June 1928, after a boiler accident on the U.S.S. Bruce, then at the Naval Shipyard, Norfolk, Va. Immediately on becoming aware of the accident, Huber without hesitation and in complete disregard of his own safety, entered the steam-filled fireroom and at grave risk to his life succeeded by almost superhuman efforts in carrying Charles H. Byran to safety. Although having received severe and dangerous burns about the arms and neck, he descended with a view toward rendering further assistance. The great courage, grit, and determination displayed by Huber on this occasion characterized conduct far above and beyond the call of duty.', '', TRUE, '', 'Pennsylvania', '1928-6-11', '?', 36, 'http://www.cmohs.org/recipient-detail/2487/huber-william-russel.php', 'Harrisburg, Pa.', 'Norfolk, VA, USA', -76, 'Huber, William Russel', 'U.S. Navy', 'Machinist''s Mate');
