INSERT INTO medal_of_honor VALUES (45, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'As captain of a gun on board the U.S.S. Richmond during action against rebel forts and gunboats and with the ram Tennessee in Mobile Bay, 5 August 1864. Despite damage to his ship and the loss of several men on board as enemy fire raked her decks, James fought his gun with skill and courage throughout a furious 2-hour battle which resulted in the surrender of the rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/691/james-john-h.php', '1835, Boston, Mass.', 'Mobile Bay, Alabama, USA', -87, 'James, John H.', 'U.S. Navy', 'Captain of the Top');
