INSERT INTO medal_of_honor VALUES (-1, '', 30, 9, 1864, -1, -1, -1, 'Having had his arm mutilated, making immediate amputation necessary, he loaded and discharged his piece with one hand and urged his men forward; this within 30 yards of the enemy''s works.', 'Company B', TRUE, '36th U.S. Colored Troops', 'Norfolk, Va.', '1864-9-30', '?', 36, 'http://www.cmohs.org/recipient-detail/692/james-miles.php', 'Princess Anne County, Va.', 'Chapin Street, Norfolk, VA 23503, USA', -76, 'James, Miles', 'U.S. Army', 'Corporal');
INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, -1, -1, -1, 'For extraordinary heroism in action on 7 April 1945 near Lippoldsberg, Germany. As lead scout during a maneuver to secure and expand a vital bridgehead, Private First Class James was the first to draw enemy fire. He was pinned down for over an hour, during which time he observed enemy positions in detail. Returning to his platoon, he assisted in working out a new plan of maneuver. He then led a squad in the assault, accurately designating targets as he advanced, until he was killed by enemy machine gun fire while going to the aid of his fatally wounded platoon leader. Private First Class James'' fearless, self-assigned actions, coupled with his diligent devotion to duty exemplified the finest traditions of the Armed Forces.', '', TRUE, '', '', '-1--1--1', '?', 51, 'http://www.cmohs.org/recipient-detail/2803/james-willy-f-jr.php', '', 'Lippoldsberg, 37194 Wahlsburg, Germany', 9, 'James, Willy F., Jr.', 'U.S. Army', 'Private First Class');
INSERT INTO medal_of_honor VALUES (-1, '', 29, 9, 1864, -1, -1, -1, 'Voluntarily went between the lines under a heavy fire at Petersburg, Va., to the assistance of a wounded and helpless officer, whom he carried within the Union lines. At Fort Harrison, Va., seized the regimental color, the color bearer and guard having been shot down, and, rushing forward, planted it upon the fort in full view of the entire brigade.', 'Company B', TRUE, '139th New York Infantry', 'New York, N.Y.', '1864-9-29', '?', 37, 'http://www.cmohs.org/recipient-detail/693/jamieson-walter.php', 'France', 'Petersburg, VA, USA', -77, 'Jamieson, Walter', 'U.S. Army', 'First Sergeant');
INSERT INTO medal_of_honor VALUES (503, 'Ohio', -1, -1, -1, 19, 3, 1873, 'On board the U.S.S. Potomac during the passage of that vessel from Cat Island to Nassau, 14 November 1898. Volunteering to enter the fireroom which was filled with steam, Jardine, after repeated attempts, succeeded in reaching the auxiliary valve and opening it, thereby relieving the vessel from further danger.', '', TRUE, '', '', '-1--1--1', '?', 25, 'http://www.cmohs.org/recipient-detail/2135/jardine-alexander.php', ' Inverness, Scotland', 'Nassau, The Bahamas', -77, 'Jardine, Alexander', 'U.S. Navy', 'Fireman First Class');
