INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Served as chief boatswain''s mate on board the U.S. Ironclad, Chickasaw, Mobile Bay, 5 August 1864. Although his enlistment was up, Jones volunteered for the battle of Mobile Bay, going on board the Chickasaw from the Vincennes where he then carried out his duties gallantly throughout the engagement with the enemy which resulted in the capture of the rebel ram Tennessee.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/714/jones-andrew.php', '1835, Ireland', 'Mobile Bay, Alabama, USA', -87, 'Jones, Andrew', 'U.S. Navy', 'Chief Boatswain''s Mate');
