INSERT INTO medal_of_honor VALUES (-1, 'California', -1, -1, -1, 1, 12, 1918, 'For conspicuous devotion to duty, extraordinary courage, and complete disregard of his own life, above and beyond the call of duty, during the attack on the Fleet in Pearl Harbor, by Japanese forces on 7 December 1941. Ens. Jones organized and led a party, which was supplying ammunition to the antiaircraft battery of the U.S.S. California after the mechanical hoists were put out of action when he was fatally wounded by a bomb explosion. When 2 men attempted to take him from the area which was on fire, he refused to let them do so, saying in words to the effect, "Leave me alone! I am done for. Get out of here before the magazines go off."', '', TRUE, '', '', '-1--1--1', '?', 21, 'http://www.cmohs.org/recipient-detail/2810/jones-herbert-charpoit.php', ' Los Angeles, Calif.', 'Pearl Harbor, HI 96706, USA', -157, 'Jones, Herbert Charpoit', 'U.S. Navy', 'Ensign');
INSERT INTO medal_of_honor VALUES (59, 'New Hampshire', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Rhode Island, which was engaged in saving the lives of the officers and crew of the Monitor, 30 December 1862. Participating in the hazardous rescue of the officers and crew of the sinking Monitor, Jones, after rescuing several of the men, became separated in a heavy gale with other members of the cutter that had set out from the Rhode Island, and spent many hours m the small boat at the mercy of the weather and high seas until finally picked up by a schooner 50 miles east of Cape Hatteras.', '', TRUE, '', '', '-1--1--1', '?', 35, 'http://www.cmohs.org/recipient-detail/716/jones-john.php', '1837, Bridgeport, Conn.', 'Cape Hatteras, North Carolina, USA', -75, 'Jones, John', 'U.S. Navy', 'Landsman');
INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Served as quartermaster on board the U.S.S. Oneida in the engagement at Mobile Bay, 5 August 1864. Stationed at the wheel during the fierce action, Jones, though wounded, carried out his duties gallantly by going to the poop to assist at the signals after the wheel ropes were shot away and remained there until ordered to reeve new wheel ropes.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/717/jones-john-e.php', '1834, New York, N.Y.', 'Mobile Bay, Alabama, USA', -87, 'Jones, John E.', 'U.S. Navy', 'Quartermaster');
INSERT INTO medal_of_honor VALUES (59, 'Maryland', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Ticonderoga during attacks on Fort Fisher, 24 and 25 December 1864; and 13 to 15 January 1865. Despite heavy return fire by the enemy and the explosion of the 100-pounder Parrott rifle which killed 8 men and wounded 12 more, Jones, as captain of a gun, performed his duties with skill and courage during the first 2 days of battle. As his ship again took position on the line on the 13th, he remained steadfast as the Ticonderoga maintained a well-placed fire upon the batteries on shore, and thereafter, as she materially lessened the power of guns on the mound which had been turned upon our assaulting columns. During this action the flag was planted on one side of the strongest fortifications possessed by the rebels.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/718/jones-thomas.php', '1820, Baltimore, Md.', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Jones, Thomas', 'U.S. Navy', 'Coxswain');
INSERT INTO medal_of_honor VALUES (45, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'As captain of a gun on board the U.S.S. Rlchmond during action against rebel forts and gunboats and with the ram Tennessee in Mobile Bay, 5 August 1864. Despite damage to his ship and the loss of several men on board as enemy fire raked her decks, Jones fought his gun with skill and courage throughout the prolonged battle which resulted in the surrender of the rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/719/jones-william.php', '1831, Philadelphia, Pa.', 'Mobile Bay, Alabama, USA', -87, 'Jones, William', 'U.S. Navy', 'Captain of the Top');
