INSERT INTO medal_of_honor VALUES (84, 'New Jersey', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Nereus during the attack on Fort Fisher, on 15 January 1865. Kane, as captain of the hold, displayed outstanding skill and courage as his ship maintained its well-directed fire against fortifications on shore despite the enemy''s return fire. When a rebel steamer was discovered in the river back of the fort, the Nereus, with forward rifle guns trained, drove the ship off at the third fire. The gallant ship''s participation contributed to the planting of the flag on one of the strongest fortifications possessed by the rebels.', '', TRUE, '', '', '-1--1--1', '?', 33, 'http://www.cmohs.org/recipient-detail/729/kane-thomas.php', '1841, Jersey City, N.J.', 'Fort Fisher Historic Museum, 1610 South Fort Fisher Boulevard, Kure Beach, NC 28449, USA', -77, 'Kane, Thomas', 'U.S. Navy', 'Captain of the Hold');
