INSERT INTO medal_of_honor VALUES (-1, '', -1, -1, -1, 21, 6, 1920, 'For conspicuous gallantry and intrepidity at the risk of his life above and beyond the call of duty as leader of a machine gun section in Company E, in action against enemy aggressor forces. With the company''s defensive perimeter overrun by a numerically superior hostile force during a savage night attack north of Yudam-ni and his platoon commander seriously wounded, S/Sgt. Kennemore unhesitatingly assumed command, quickly reorganized the unit and directed the men in consolidating the position. When an enemy grenade landed in the midst of a machine gun squad, he bravely placed his foot on the missile and, in the face of almost certain death, personally absorbed the full force of the explosion to prevent injury to his fellow marines. By his indomitable courage, outstanding leadership and selfless efforts in behalf of his comrades, S/Sgt. Kennemore was greatly instrumental in driving the enemy from the area and upheld the highest traditions of the U.S. Naval Service.', 'Company E, 2d Battalion', TRUE, '7th Marines, 1st Marine Division', 'Greenville, S.C.', '-1--1--1', '?', 35, 'http://www.cmohs.org/recipient-detail/3138/kennemore-robert-s.php', ' Greenville, S.C.', 'South Korea', 127, 'Kennemore, Robert S.', 'U.S. Marine Corps', 'Staff Sergeant');
