INSERT INTO medal_of_honor VALUES (45, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'Served on board the U.S.S. Wyalusing and participated in a plan to destroy the rebel ram Albemarle in Roanoke River, 25 May 1864. Volunteering for the hazardous mission, Lafferty participated in the transfer of two torpedoes across an island swamp and then served as sentry to keep guard of clothes and arms left by other members of the party. After being rejoined by others of the party who had been discovered before the plan could be completed, Lafferty succeeded in returning to the mother ship after spending 24 hours of discomfort in the rain and swamp.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/778/lafferty-john.php', '1842, New York, N.Y.', 'Roanoke River, North Carolina, USA', -77, 'Lafferty, John', 'U.S. Navy', 'Fireman');
INSERT INTO medal_of_honor VALUES (32, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'Off Yazoo City, Miss., 5 March 1864, embarking from the Marmora with a 12-pound howitzer mounted on a field carriage, Laffey landed with the gun and crew in the midst of heated battle and, bravely standing by his gun despite enemy rifle fire which cut the gun carriage and rammer, contributed to the turning back of the enemy during the fierce engagement.', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/779/laffey-bartlett.php', '1841, Ireland', 'Yazoo City, MS 39194, USA', -90, 'Laffey, Bartlett', 'U.S. Navy', 'Seaman');
