INSERT INTO medal_of_honor VALUES (11, 'Maryland', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Commodore Perry in the attack upon Franklin, Va., 3 October 1862. With enemy fire raking the deck of his ship and blockades thwarting her progress, Lakin remained at his post and performed his duties with skill and courage as the Commodore Perry fought a gallant battle to silence many rebel batteries as she steamed down the Blackwater River.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/781/lakin-daniel.php', '1834, Baltimore Md.', 'Franklin, VA, USA', -76, 'Lakin, Daniel', 'U.S. Navy', 'Seaman');
