INSERT INTO medal_of_honor VALUES (45, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'On board the flagship U.S.S. Hartford during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay on 5 August 1864. Wounded in the leg and thrown violently against the side of the ship when an enemy shell killed or wounded the 6-man crew as the shell whipped on the berth deck, Lawson, upon regaining his composure, promptly returned to his station and, although urged to go below for treatment, steadfastly continued his duties throughout the remainder of the action.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/790/lawson-john.php', '1837, Pennsylvania', 'Fort Morgan, 51 Alabama 180, Gulf Shores, AL 36542, USA', -88, 'Lawson, John', 'U.S. Navy', 'Landsman');
