INSERT INTO medal_of_honor VALUES (32, 'Georgia', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Lehigh, Charleston Harbor, 16 November 1863, during the hazardous task, of freeing the Lehigh, which had grounded, and was under heavy enemy fire from Fort Moultrie. Rowing the small boat which was used in the hazardous task of transferring hawsers from the Lehigh to the Nahant, Leland twice succeeded in making the trip, only to find that each had been in vain when the hawsers were cut by enemy fire and chaffing.', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/794/leland-george-w.php', '1834, Savannah, Ga.', 'Charleston Harbor, South Carolina, USA', -79, 'Leland, George W.', 'U.S. Navy', 'Gunner''s Mate');
