INSERT INTO medal_of_honor VALUES (45, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Wyalusing and participating in a plan to destroy the rebel ram Albemarle in Roanoke River, 25 May 1864. Volunteering for the hazardous mission, Lloyd participated in the transfer of two torpedoes across an island swamp. Serving as boatkeeper, he aided in rescuing others of the party who had been detected before the plan could be completed, but who escaped, leaving detection of the plan impossible. By his skill and courage, Lloyd succeeded in returning to the mother ship after spending 24 hours of discomfort in the rain and swamp.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/808/lloyd-benjamin.php', '1839, England', 'Roanoke River, North Carolina, USA', -77, 'Lloyd, Benjamin', 'U.S. Navy', 'Coal Heaver');
INSERT INTO medal_of_honor VALUES (45, 'New York', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Wyalusing during an attempt to destroy the rebel ram Albemarle in Roanoke River, 25 May 1864, Lloyd participated in this daring plan by swimming the Roanoke River heavily weighted with a line which was used for hauling torpedoes across. Thwarted by discovery just before the completion of the plan, Lloyd cut the torpedo guiding line to prevent detection of the plan by the enemy and again swam the river, narrowly escaping enemy musket fire and regaining the ship in safety.', '', TRUE, '', '', '-1--1--1', '?', 36, 'http://www.cmohs.org/recipient-detail/809/lloyd-john-w.php', '1831, New York, N.Y.', 'Roanoke River, North Carolina, USA', -77, 'Lloyd, John W.', 'U.S. Navy', 'Coxswain');
