INSERT INTO medal_of_honor VALUES (59, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Rhode Island which was engaged in rescuing men from the stricken Monitor in Mobile Bay, on 30 December 1862. After the Monitor sprang a leak and went down, Logan courageously risked his life in a gallant attempt to rescue members of the crew. Although sacrificing his life during the hazardous operation, he had made every effort possible to save the lives of his fellow men.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/811/logan-hugh.php', '1834, Ireland', 'Mobile Bay, Alabama, USA', -87, 'Logan, Hugh', 'U.S. Navy', 'Captain of the Afterguard');
