INSERT INTO medal_of_honor VALUES (45, 'Pennsylvania', -1, -1, -1, -1, -1, -1, 'As captain of a gun on board the U.S.S. Richmond during action against rebel forts and gunboats and with the ram Tennessee in Mobile Bay, 5 August 1864. Despite damage to his ship and the loss of several men on board as enemy fire raked her decks, Sgt. Martin fought his gun with skill and courage throughout the furious 2_hour battle which resulted in the surrender of the rebel ram Tennessee and in the damaging and destruction of batteries at Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/852/martin-james.php', '1826, Derry, Ireland', 'Mobile Bay, Alabama, USA', -87, 'Martin, James', 'U.S. Marine Corps', 'Sergeant');
