INSERT INTO medal_of_honor VALUES (17, 'New York', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Richmond in the attack on Port Hudson, 14 March 1863. Damaged by a 6_inch solid rifle shot which shattered the starboard safety_valve chamber and also damaged the port safety_valve, the fireroom of the Richmond immediately became filled with steam to place it in an extremely critical condition. Acting courageously in this crisis, McClelland persisted in penetrating the steam_filled room in order to haul the hot fires of the furnaces and continued this gallant action until the gravity of the situation had lessened.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/875/mcclelland-matthew.php', '1833, Brooklyn, N.Y.', 'Port Hudson, LA 70791, USA', -91, 'Mcclelland, Matthew', 'U.S. Navy', 'First Class Fireman');
