INSERT INTO medal_of_honor VALUES (45, 'Maine', -1, -1, -1, -1, -1, -1, 'On board the U.S.S. Lackawanna during successful attacks against Fort Morgan, rebel gunboats and the ram Tennessee in Mobile Bay, on 5 August 1864. Wounded when an enemy shell struck, and ordered to go below, McCullock refused to leave his station and continued to perform his duties throughout the prolonged action which resulted in the capture of the prize ram Tennessee and in the damaging and destruction of Fort Morgan.', '', TRUE, '', '', '-1--1--1', '?', 30, 'http://www.cmohs.org/recipient-detail/879/mccullock-adam.php', '1830, Maine', 'Mobile Bay, Alabama, USA', -87, 'Mccullock, Adam', 'U.S. Navy', 'Seaman');
