INSERT INTO medal_of_honor VALUES (-1, '', 23, 5, 1939, 15, 7, 1900, 'For extraordinary heroism in the line of his profession as a master diver throughout the rescue and salvage operations following the sinking of the U.S.S. Squalus on 23 May 1939. His leadership, masterly skill, general efficiency, and untiring devotion to duty in directing diving operations, and in making important and difficult dives under the most hazardous conditions, characterize conduct far above and beyond the ordinary call of duty.', '', TRUE, '', 'Washington, D.C.', '1939-5-23', '?', -1, 'http://www.cmohs.org/recipient-detail/2490/mcdonald-james-harper.php', ' Scotland', '', -1, 'Mcdonald, James Harper', 'U.S. Navy', 'Chief Metalsmith');
INSERT INTO medal_of_honor VALUES (11, 'Massachusetts', -1, -1, -1, -1, -1, -1, 'Serving on board the U.S.S. Baron De Kalb, Yazoo River Expedition, 23 to 27 December 1862. Proceeding under orders up the Yazoo River, the U.S.S. Baron De Kalb, with the object of capturing or destroying the enemy''s transports, came upon the steamers John Walsh, R. J. Locklan, Golden Age, and the Scotland, sunk on a bar where they were ordered burned. Continuing up the river, she was fired on but, upon returning the fire, caused the enemy''s retreat. Returning down the Yazoo, she destroyed and captured large quantities of enemy equipment and several prisoners. Serving bravely throughout this action, McDonald, as boatswain''s mate, "distinguished himself in the various actions."', '', TRUE, '', '', '-1--1--1', '?', 32, 'http://www.cmohs.org/recipient-detail/881/mcdonald-john.php', '1817, Scotland', 'Yazoo River, 1, LA, USA', -90, 'Mcdonald, John', 'U.S. Navy', 'Boatswain''s Mate');
