INSERT INTO medal_of_honor VALUES (11, 'Maine', -1, -1, -1, -1, -1, -1, 'Captain of foretop, and a volunteer from the Colorado, McLeod served on board the U.S.S. Pensacola during the attack upon Forts Jackson and St. Philip and the taking of New Orleans, 24 and 25 April 1862. Acting as gun captain of the rifled howitzer aft which was much exposed, he served this piece with great ability and activity, although no officer superintended it.', '', TRUE, '', '', '-1--1--1', '?', 44, 'http://www.cmohs.org/recipient-detail/903/mcleod-james.php', 'Scotland', 'Fort Jackson, NY 12965, USA', -74, 'Mcleod, James', 'U.S. Navy', 'Captain of the Top');
